import React from 'react';
import { Platform, StyleSheet, Text, View, Button } from 'react-native';
import * as firebase from 'firebase';
import * as Font from 'expo-font';
import { color } from 'react-native-reanimated';

export default class Loading extends React.Component {
    componentDidMount() {
        firebase.auth().onAuthStateChanged(user => {
            this.props.navigation.navigate(user ? 'Main' : 'Login')
        })
    }

    constructor(props) {
        super(props);

        this.state = { isReady: false };
    }

    async componentDidMount() {

        await Font.loadAsync({
            'Cafe24Ohsquare': require('../assets/fonts/Cafe24Ohsquare.ttf'),
        });

        this.setState({ isReady: true });
    }

    render() {
        return (
            <View style={styles.container}>
                <View style={styles.halfContainer}>
                    <Text style={styles.text_title}>COVID19 특별 복지 제도 알림 앱입니다.</Text>
                    <Text style={styles.text_subtitle}>맞춤 검색을 통해 본인에게 알맞는 복지를 찾아보세요.</Text>
                    <View style={styles.halfContainer}>

                        <Button
                            title="시작하기"
                            style={styles.start_button}
                            rounded
                            onPress={() => this.props.navigation.navigate('Login')}>
                            <Text style={styles.start_text}>시작하기</Text>
                        </Button>

                    </View>
                </View>
            </View >
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center"
    },
    halfContainer: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center"
    },
    text_title: {
        padding: 20,
        marginTop: 20,
        fontFamily: 'Cafe24Ohsquare',
        fontSize: 32,
        textAlign: "center",
        color: 'tomato'
    },
    text_subtitle: {
        fontFamily: 'Cafe24Ohsquare',
        fontSize: 20,
        textAlign: "center",
        color: '#5c5c5c'
    },
    start_button: {
        alignSelf: 'center',
        justifyContent: 'center',
        width: 200,
        height: 50,
        color: "tomato"
    },
    start_text: {
        fontFamily: 'Cafe24Ohsquare',
        fontSize: 50,
        alignItems: "center",
        justifyContent: "center"
    }

});
