import React, { Component } from 'react';
import { StyleSheet, View, Text } from 'react-native';

import * as firebase from "firebase/app";
import "firebase/firebase-database";
import firebaseConfig from '../../src/config/fire';

import * as Font from 'expo-font';
import Constants from 'expo-constants';
import { Card, CardItem, Thumbnail, Body, Left, Right, Button, Icon, Item } from 'native-base';
import { ScrollView } from 'react-native-gesture-handler';

var dataTemp = [];
var personalDataTemp = [];

if (!firebase.apps.length) {
    firebase.initializeApp(firebaseConfig);
}

export default class PersonalSearchTab extends Component {
    state = { currentUser: null }
    componentDidMount() {
        const { currentUser } = firebase.auth()
        this.setState({ currentUser })
    }

    constructor(props) {
        super(props);
        this.state = {
            data: [],
            userInfo: [],
            personalData: [],
            isReady: false
        };
    }

    componentWillMount() {
        const ref = firebase.database().ref();
        ref.on('value', snapshot => {
            this.setState({ data: snapshot.val() });
            //console.log(Object.values(this.state.data[0].지원대상).join(" "));

            for (var i = 0; i < this.state.data.length; i++) {
                dataTemp.push(Object.values(this.state.data[i].지원대상).join(" "));
            }
        });
    }

    async componentDidMount() {

        await Font.loadAsync({
            'Cafe24Ohsquare': require('../../assets/fonts/Cafe24Ohsquare.ttf'),
            'Cafe24Ohsquareair': require('../../assets/fonts/Cafe24Ohsquareair.ttf'),

        });

        this.setState({ isReady: true });
    }

    getUserInfo() {
        var user = firebase.auth().currentUser;
        var list = [];
        try {
            if (user != null) {
                firebase.firestore().collection('users').doc(user.uid)
                    .onSnapshot(doc => {
                        list.push(doc.data());
                        this.setState({
                            userInfo: list
                        })
                        //console.log(this.state.userInfo[0].job[0]);
                    });

            }
        }
        catch (error) {
            alert(error)
        }
    }

    compareTo() {
        this.getUserInfo();

        this.state.userInfo.map(value => {
            //console.log(Object.values(this.state.data[4].서비스명).join(" "));
            //console.log(value.job[0]);
            for (var i = 0; i < dataTemp.length; i++) {
                if (dataTemp[i].indexOf(value.job[0])) {
                    personalDataTemp.push(Object.values(this.state.data[i].서비스명).join(" "));
                    personalDataTemp.push(Object.values(this.state.data[i].구비서류).join(" "));
                    personalDataTemp.push(Object.values(this.state.data[i].지원대상).join(" "));
                }
                else {
                    i++;
                    console.log('done');
                }
            }
            this.setState.personalData({ personalDataTemp });

            for (var j = 0; j < personalDataTemp.length; j++) {
                console.log(personalDataTemp[j]);
            }
            console.log(personalDataTemp.length);
        })
    }

    render() {
        var user = firebase.auth().currentUser;
        var name;

        if (user != null) {
            name = user.displayName;
        }

        return (
            <ScrollView>
                {
                    this.state.personalData.map(value => {

                        return (
                            <View style={{ justifyContent: 'space-between' }}>
                                <Text style={styles.user}>안녕하세요 {name} 님</Text>
                                <View style={styles.container}>
                                    <Text style={styles.header}>사용자 정보 기반 맞춤 복지 검색 결과입니다.</Text>
                                </View>
                                <View style={styles.container2}>
                                    <Button
                                        title="정보 확인"
                                        rounded
                                        style={styles.check_button}
                                        onPress={() => this.compareTo()}>
                                        <Text style={styles.check_button_text}>정보 확인하기</Text>
                                    </Button>
                                </View>
                                <View style={styles.container}>
                                    <Card style={styles.card_style}>
                                        <CardItem>
                                            <Text style={fontFamily = "Cafe24Ohsquareair"}>{value}</Text>
                                        </CardItem>
                                    </Card>
                                </View>
                            </View>
                        )
                    })
                }
            </ScrollView>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
        flexGrow: 1,
        marginTop: Constants.statusBarHeight,
    },
    container2: {
        flex: 2,
        alignItems: 'center',
        justifyContent: 'center',
    },
    header: {
        fontFamily: 'Cafe24Ohsquare',
        fontSize: 20,
        color: 'tomato'
    },
    user: {
        fontFamily: 'Cafe24Ohsquareair',
        padding: 10,
        alignSelf: "flex-end",
        marginTop: 10
    },
    check_button: {
        alignSelf: 'center',
        justifyContent: 'center',
        width: 100,
        height: 30,
        backgroundColor: 'white',
        marginTop: 100
    },
    check_button_text: {
        fontFamily: 'Cafe24Ohsquareair',
        color: '#5c5c5c'
    },
    card_style: {
        marginTop: '2%',
        marginLeft: '2%',
        marginRight: '2%',
        marginBottom: '2%'
    },
});
