import React, { Component } from 'react';
import { StyleSheet, View, Text } from 'react-native';

//import * as firebase from 'firebase';
import * as firebase from "firebase/app";
import "firebase/auth";
import XMLParser from 'react-xml-parser';

import * as Font from 'expo-font';
import { ScrollView } from 'react-native-gesture-handler';
import { Grid, Col, Thumbnail, Body, Left, Right, Button, Icon } from 'native-base';
import Box from '@material-ui/core/Box';


export default class MainScreen extends Component {
    state = { currentUser: null }
    componentDidMount() {
        const { currentUser } = firebase.auth()
        this.setState({ currentUser })
    }

    constructor(props) {
        super(props);
        this.state = {
            status: [],
            isReady: false
        };
    }

    async componentDidMount() {

        await Font.loadAsync({
            'Cafe24Ohsquare': require('../assets/fonts/Cafe24Ohsquare.ttf'),
            'Cafe24Ohsquareair': require('../assets/fonts/Cafe24Ohsquareair.ttf'),

        });

        this.setState({ isReady: true });

        await this.fetchData();
    }

    fetchData() {
        const parseString = require('react-native-xml2js').parseString;
        var DOMParser = require('xmldom').DOMParser;

        const API_KEY = "jAw1w75sOgvmaIdrvOV8q6x6bUUUMw5PJibAZB8QwxVBOMY1Rx4j9MdXFABH51e5WOMgXbS4qnV%2BwWJ7zoC%2Bpg%3D%3D";
        const API_STEM = "http://openapi.data.go.kr/openapi/service/rest/Covid19/getCovid19SidoInfStateJson"
        var url = `${API_STEM}?serviceKey=${API_KEY}&pageNo=1&numOfRows=10&startCreateDt=20200617&endCreateDt=20200617`;

        let list = [];
        let temp = null;

        return fetch(url)
            .then(response => response.text())
            .then(data => {
                //console.log(data);
                //parseString(data, function (err, result) {
                //console.dir(result);
                //console.log(JSON.stringify(result));
                //console.log(JSON.parse(JSON.stringify(result)));

                var xmlDoc = new DOMParser().parseFromString(data, 'text/xml');
                var x = xmlDoc.getElementsByTagName("item");

                for (var i = 1; i < x.length; i++) {
                    var nodeList = x[i].childNodes;

                    for (var j = 0; j < nodeList.length; j++) {
                        var item = nodeList[j];

                        if (item.firstChild) {
                            //console.log(item.nodeName + " : " + item.childNodes[0].nodeValue);
                            if ((item.nodeName) == ("gubun")) {
                                temp = item.childNodes[0].nodeValue;

                            }
                            else if ((item.nodeName) == ("incDec")) {
                                list.push({
                                    si: temp,
                                    incDec: item.childNodes[0].nodeValue
                                })
                            }
                        }
                    }
                }
                this.setState({ status: list });

                for (var k = 0; k < list.length; k++) {
                    console.log(list[k]);
                }
                return xmlDoc;

            })
            .catch(error => {
                console.log(error);
            })

    }

    logoutUser = () => {
        try {
            firebase.auth().signOut().then(() => {
                this.props.navigation.navigate('LoadingScreen')
            })
        }
        catch (error) {
            alert(error.toString())
        }

    }

    render() {
        var user = firebase.auth().currentUser;
        var name;

        if (user != null) {
            name = user.displayName;
        }

        return (
            <ScrollView>
                <View style={{ justifyContent: 'space-between' }}>
                    <Text style={styles.user}>안녕하세요 {name} 님</Text>
                    <Button
                        title="logout"
                        style={styles.button_logout}
                        onPress={() => this.logoutUser()}>
                        <Text style={styles.button_logout_text}>logout</Text>
                    </Button>
                    <View style={styles.container}>
                        <Text style={styles.header}>CoWel</Text>
                        <Text style={styles.sub}> COVID19 NEWS </Text>

                    </View>
                </View>
                <View style={styles.secondContainer}>

                    {
                        this.state.status.map(value => {
                            return (

                                <View style={styles.thirdContainer}>
                                    <Grid>
                                        <Col>
                                            <Box
                                                display="flex"
                                                p={1}

                                                justifyContent="center"
                                                bgcolor="background.paper"
                                                css={{ maxWidth: 500 }}
                                                boxShadow={3}
                                                borderRadius={16}
                                                borderColor="tomato">

                                                <Text style={styles.covid_news}> 지역: {value.si} -> </Text>
                                                <Text style={styles.covid_news}> 확진자 전일 대비 증감 수: {value.incDec}</Text>


                                            </Box>
                                        </Col>
                                        <Col>
                                        </Col>
                                    </Grid>

                                </View>

                            )
                        })

                    }

                </View>

            </ScrollView>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
    },
    header: {
        fontFamily: 'Cafe24Ohsquare',
        padding: 10,
        marginTop: 50,
        fontSize: 100,
        color: 'tomato'

    },
    sub: {
        fontFamily: 'Cafe24Ohsquareair',
        padding: 10,
        marginTop: 20,
        fontSize: 30,
        color: '#5c5c5c',

    },
    user: {
        fontFamily: 'Cafe24Ohsquareair',
        padding: 10,
        alignSelf: "flex-end",
        marginTop: 10
    },
    button_logout: {
        justifyContent: "space-between",
        alignSelf: "flex-end",
        backgroundColor: "white",
        marginRight: 30,
        width: 50
    },
    button_logout_text: {
        color: "#5c5c5c",
        alignItems: "center",
        justifyContent: "center",
        textAlign: 'center',
        fontFamily: "Cafe24Ohsquareair",
        fontSize: 15
    },
    secondContainer: {
        flex: 2,
        justifyContent: "flex-start",
    },
    thirdContainer: {
        flex: 3,
        marginLeft: 20
    },
    covid_news: {
        fontFamily: 'Cafe24Ohsquareair',
        marginTop: 5,
        marginBottom: 5
    }

});
