import React from 'react';

import { Platform, StyleSheet, Text, View } from 'react-native';
import * as firebase from 'firebase';

import * as Font from 'expo-font';
import { Button, Container, Content, Header, Form, Input, Item, Label } from 'native-base';

export default class Loading extends React.Component {
    componentDidMount() {
        firebase.auth().onAuthStateChanged(user => {
            this.props.navigation.navigate(user ? 'Main' : 'Login')
        })
    }

    constructor(props) {
        super(props);

        this.state = { isReady: false };
    }

    async componentDidMount() {

        await Font.loadAsync({
            'Cafe24Ohsquare': require('../assets/fonts/Cafe24Ohsquare.ttf'),
            'Cafe24Ohsquareair': require('../assets/fonts/Cafe24Ohsquareair.ttf'),
        });

        this.setState({ isReady: true });
    }

    componentWillMount() {
        fetch('http://openapi.data.go.kr/openapi/service/rest/Covid19/getCovid19SidoInfStateJson?serviceKey=jAw1w75sOgvmaIdrvOV8q6x6bUUUMw5PJibAZB8QwxVBOMY1Rx4j9MdXFABH51e5WOMgXbS4qnV%2BwWJ7zoC%2Bpg%3D%3D&pageNo=1&numOfRows=10&startCreateDt=20200410&endCreateDt=20200410&')
            .then((response) => response.text())
            .then((responseText) => {
                console.log("res", responseText);
                const match = responseText.match(/\((.*)\)/); // 정규식은 JSONP응답에 따라 달라질 수 있음. 이 경우 ( 응답 )이고 match는 ( ) 를 포함한 전체 응답과 () 내부의 데이터가 배열 형태로 출력됨 
                if (match == null) Alert.alert("Message", "null");
                Alert.alert("1", match[0].toString());
                console.log("2", match[1].toString());

                let jsonResult = JSON.parse(match[1].toString()); // ( ) 를 뗀 진짜 json 형태의 데이터를 파싱하고
                //let jusoArray = jsonResult.results.juso; // juso 속성도 array 형태이기 때문에 또 받아주고,
                console.log("result", jsonResult.results.juso.toString());
                //console.log("juso", JSON.stringify(jsonResult));//이건 확인용 
                //console.log("real data", jusoArray[0].detBdNmList);// juso 배열읭 첫번째 데이터의 detBdNmList 속성 값을 출력해주었다.
            })
            .catch((error) => {
                console.error(error);
            });
    }

    render() {
        return (
            <View style={styles.container}>
                <View style={styles.halfContainer}>
                    <Text style={styles.text_title}>COVID19 특별 복지 제도 알림 앱입니다.</Text>
                    <Text style={styles.text_subtitle}>맞춤 검색을 통해 본인에게 알맞는 복지를 찾아보세요.</Text>
                    <View style={styles.halfContainer}>

                        <Button
                            title="시작하기"
                            style={styles.start_button}
                            rounded
                            onPress={() => this.props.navigation.navigate('Login')}>
                            <Text style={styles.start_text}>시작하기</Text>
                        </Button>

                    </View>
                </View>
            </View >
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center"
    },
    halfContainer: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center"
    },
    text_title: {
        padding: 20,
        marginTop: 20,
        fontFamily: 'Cafe24Ohsquare',
        fontSize: 32,
        textAlign: "center",
        color: 'tomato'
    },
    text_subtitle: {
        fontFamily: 'Cafe24Ohsquare',
        fontSize: 20,
        textAlign: "center",
        color: '#5c5c5c'
    },
    start_button: {
        alignSelf: 'center',
        justifyContent: 'center',
        width: 200,
        height: 50,
        backgroundColor: 'tomato'
    },
    start_text: {
        fontFamily: 'Cafe24Ohsquareair',
        fontSize: 30,
        alignItems: "center",
        justifyContent: "center",
        color: "white"
    }

})
