import React, { Component } from 'react';
import { Platform, StyleSheet, Text, View, TextInput } from 'react-native';

import * as firebase from "firebase/app";
import "firebase/auth";
import firebaseConfig from '../src/config/fire';

import * as Font from 'expo-font';
import { Button, Container, Content, Header, Form, Input, Item, Label } from 'native-base';


if (!firebase.apps.length) {
    firebase.initializeApp(firebaseConfig);
}

export default class Login extends React.Component {

    constructor(props) {
        super(props)

        this.state = ({
            email: '',
            password: '',
            isReady: false,
        })
    }

    async componentDidMount() {

        await Font.loadAsync({
            'Cafe24Ohsquare': require('../assets/fonts/Cafe24Ohsquare.ttf'),
        });

        this.setState({ isReady: true });
    }

    signUpUser = (email, password) => {
        try {
            if (this.state.password.length < 6) {
                alert("please enter at least 6 characters")
                return;
            }
            firebase.auth().createUserWithEmailAndPassword(email, password)
        }
        catch (error) {
            console.log(error.toString())
        }

    }
    loginUser = (email, password) => {
        try {
            firebase.auth().signInWithEmailAndPassword(email, password).then(() => {
                this.props.navigation.navigate('MainScreen')
            })
        } catch (error) {
            console.log(error.toString())
        }

    }

    render() {
        return (
            <Container style={styles.container}>
                <Text>Login</Text>
                <Form>
                    <Item floatingLabel>
                        <Label style={{ fontFamily: 'Cafe24Ohsquare' }}>Email</Label>
                        <Input
                            autoCapitalize="none"
                            autoCorrect={false}
                            onChangeText={(email) => this.setState({ email })}
                            style={styles.form}
                        />
                    </Item>

                    <Item floatingLabel>
                        <Label style={{ fontFamily: 'Cafe24Ohsquare' }}>Password</Label>
                        <Input
                            secureTextEntry={true}
                            autoCapitalize="none"
                            autoCorrect={false}
                            onChangeText={(password) => this.setState({ password })}
                            style={styles.form}
                        />
                    </Item>

                    <Button
                        title="login"
                        style={styles.button}
                        rounded
                        success
                        onPress={() => this.loginUser(this.state.email, this.state.password)}>
                        <Text style={styles.button_text}>Login</Text>
                    </Button>

                    <Button
                        title="signup"
                        style={styles.button}
                        rounded
                        primary
                        onPress={() => this.signUpUser(this.state.email, this.state.password)}>
                        <Text style={styles.button_text}>Signup</Text>
                    </Button>
                </Form>
            </Container>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#E0ECF8",
        justifyContent: "center",
        padding: 20
    },
    button_text: {
        color: "white",
        alignItems: "center",
        justifyContent: "center",
        fontFamily: "Cafe24Ohsquare",
        fontSize: 30
    },
    button: {
        marginBottom: 20,
        marginTop: 50,
        padding: 16,
        width: 200,
        alignSelf: "center",
        justifyContent: "center"
    },
    form: {
        marginTop: 20,
        marginBottom: 20,
        fontFamily: "Cafe24Ohsquare"
    }
})
