import React, { Component } from 'react';
import { StyleSheet, View, Text } from 'react-native';

import * as firebase from "firebase/app";
import "firebase/firebase-database";
import firebaseConfig from '../src/config/fire';

import { Card, CardItem, Thumbnail, Body, Left, Right, Button, Icon, Item } from 'native-base';

export default class Welfare extends Component {
    constructor(props) {
        super(props);
        this.state = ({
            data: [],
            isReady: false
        });
    }

    async componentDidMount() {

        await Font.loadAsync({
            'RIDIBatang': require('../assets/fonts/RIDIBatang.ttf'),
        });

        this.setState({ isReady: true });
    }


    componentDidMount() {
        if (!firebase.apps.length) {
            firebase.initializeApp(firebaseConfig);
        }

        const ref = firebase.database().ref();
        ref.on('value', snapshot => {
            this.setState({ data: snapshot.val() });
        });

    }

    render() {

        return (
            <View>
                {
                    this.state.data.map(value => {
                        return (
                            <ScrollView style={styles.container}>

                                <Card
                                    style={styles.card_style}>
                                    <CardItem header>
                                        <Text style={styles.font}>
                                            구비서류
                                        </Text>
                                    </CardItem>
                                    <CardItem>
                                        <Text style={styles.font}>{value.구비서류}</Text>
                                    </CardItem>
                                </Card>
                            </ScrollView>
                        )
                    })
                }
            </View>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
        marginTop: Constants.statusBarHeight,
    },
    card_style: {
        marginTop: '2%',
        marginLeft: '2%',
        marginRight: '2%',
        marginBottom: '2%'
    },
    font: {
        fontFamily: 'RIDIBatang',
        fontsize: 15
    }

})
