import React, { Component } from 'react';
import { StyleSheet, View, Text, Picker } from 'react-native';

import * as firebase from "firebase/app";
import "firebase/firebase-database";
import firebaseConfig from '../../src/config/fire';

import * as Font from 'expo-font';
import { Button, Container, Form, Input, Item, Label } from 'native-base';
import { ScrollView } from 'react-native-gesture-handler';

if (!firebase.apps.length) {
    firebase.initializeApp(firebaseConfig);
}

export default class MyPageTab extends Component {
    state = { currentUser: null }
    componentDidMount() {
        const { currentUser } = firebase.auth()
        this.setState({ currentUser })
    }

    constructor(props) {
        super(props);
        this.getUserInfo();
        this.state = { isReady: false };
    }

    async componentDidMount() {

        await Font.loadAsync({
            'Cafe24Ohsquare': require('../../assets/fonts/Cafe24Ohsquare.ttf'),
            'Cafe24Ohsquareair': require('../../assets/fonts/Cafe24Ohsquareair.ttf'),

        });

        this.setState({ isReady: true });
    }

    getUserInfo = async () => {
        var user = firebase.auth().currentUser;
        try {
            if (user != null) {
                firebase.firestore().collection('users').doc(user.uid)
                    .get()
                    .then(snapshot => {
                        const userInfo = []
                        snapshot.forEach(doc => {
                            const data = doc.data()
                            userInfo.push(data)
                        })
                        this.setState({ userInfo: userInfo })
                    })
            }
        }
        catch (error) {
            alert(error)
        }
    }

    render() {
        var user = firebase.auth().currentUser;
        var name;

        if (user != null) {
            name = user.displayName;
        }
        {
            this.state.userInfo && this.state.userInfo.map(userInfo => {
                return (
                    <View style={{ justifyContent: 'space-between' }}>
                        <Text style={styles.user}>안녕하세요 {name} 님</Text>
                        <View style={styles.container}>
                            <Text style={styles.header}>My Page</Text>
                        </View>
                        <View>
                            <Text>{userInfo.displayName}</Text>
                            <Text>{userInfo.family}</Text>
                            <Text>{userInfo.age}</Text>
                            <Text>{userInfo.Quintile}</Text>
                            <Text>{userInfo.gender}</Text>
                            <Text>{userInfo.child}</Text>
                            <Text>{userInfo.job}</Text>
                        </View>
                    </View>
                );
            })
        }
    }
}


const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    header: {
        fontFamily: 'Cafe24Ohsquare',
        fontSize: 20,
        color: 'tomato'
    },
    user: {
        fontFamily: 'Cafe24Ohsquareair',
        padding: 10,
        alignSelf: "flex-end",
        marginTop: 10
    },
});
