import React, { useReducer } from 'react'
import { StyleSheet, Text, TextInput, View, Button } from 'react-native'

import * as firebase from "firebase/app";
import "firebase/auth";
import firebaseConfig from '../src/config/fire';
import { Toast } from 'reactstrap';

if (!firebase.apps.length) {
    firebase.initializeApp(firebaseConfig);
}

export default class SignUp extends React.Component {
    constructor(props) {
        super(props)

        this.state = ({
            name: '',
            email: '',
            password: '',
            isReady: false,
        })
    }

    async componentDidMount() {

        await Font.loadAsync({
            'Cafe24Ohsquare': require('../assets/fonts/Cafe24Ohsquare.ttf'),
            'Cafe24Ohsquareair': require('../assets/fonts/Cafe24Ohsquareair.ttf'),
        });

        this.setState({ isReady: true });
    }

    signUpUser = (email, password) => {
        try {
            if (this.state.password.length < 6) {
                alert("please enter at least 6 characters")
                return;
            }
            firebase.auth().createUserWithEmailAndPassword(email, password).then(() => {
                user.updateProfile({
                    displayName: name
                }).then(function () {
                    console.log(name)
                }, function (error) {
                    alert(error.toString())
                });
                this.props.navigation.navigate('SetProfileScreen')
            })
        }
        catch (error) {
            alert(error.toString())
        }

    }

    render() {
        return (
            <Container style={styles.container}>
                <Form>
                    <Item floatingLabel>
                        <Label style={{ fontFamily: 'Cafe24Ohsquareair' }}>Name</Label>
                        <Input
                            autoCapitalize="none"
                            autoCorrect={false}
                            onChangeText={(name) => this.setState({ name })}
                            style={styles.form}
                        />
                    </Item>

                    <Item floatingLabel>
                        <Label style={{ fontFamily: 'Cafe24Ohsquareair' }}>Email</Label>
                        <Input
                            autoCapitalize="none"
                            autoCorrect={false}
                            onChangeText={(email) => this.setState({ email })}
                            style={styles.form}
                        />
                    </Item>

                    <Item floatingLabel>
                        <Label style={{ fontFamily: 'Cafe24Ohsquareair' }}>Password</Label>
                        <Input
                            secureTextEntry={true}
                            autoCapitalize="none"
                            autoCorrect={false}
                            onChangeText={(password) => this.setState({ password })}
                            style={styles.form}
                        />
                    </Item>

                    <Button
                        title="signup"
                        style={styles.button_signup}
                        rounded
                        backgroundColor='#5c5c5c'
                        onPress={() => this.props.navigation.navigate('SignUp')}
                        onPress={() => this.signUpUser(this.state.email, this.state.password)}>
                        <Text style={styles.button_text}>Signup</Text>
                    </Button>
                </Form>
            </Container>

        );

    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: "center",
        padding: 20
    },
    button_text: {
        color: "white",
        alignItems: "center",
        justifyContent: "center",
        fontFamily: "Cafe24Ohsquareair",
        fontSize: 25
    },
    button_login: {
        marginBottom: 10,
        marginTop: 30,
        padding: 30,
        width: 200,
        alignSelf: "center",
        justifyContent: "center",
        backgroundColor: "tomato"
    },
    button_signup: {
        marginBottom: 10,
        marginTop: 30,
        padding: 30,
        width: 200,
        alignSelf: "center",
        justifyContent: "center",
        backgroundColor: "#5c5c5c"
    },
    form: {
        marginTop: 20,
        marginBottom: 20,
        fontFamily: "Cafe24Ohsquareair"
    }
})
