import React, { Component } from 'react';
import { StyleSheet, View, Text, Picker } from 'react-native';

import * as firebase from "firebase/app";
import "firebase/firebase-database";
import firebaseConfig from '../src/config/fire';

import * as Font from 'expo-font';
import { Button, Container, Form, Input, Item, Label } from 'native-base';
import { DropDown } from "react-native-material-dropdown"
import { ScrollView, TouchableHighlight } from 'react-native-gesture-handler';
import { Modal } from 'reactstrap';

if (!firebase.apps.length) {
    firebase.initializeApp(firebaseConfig);
}

export default class SetProfile extends Component {
    constructor(props) {
        super(props);
        this.state = {
            profile: {
                age: '',
                address: '',
                Quintile: '',
                gender: '',
                family: '',
                job: ''
            },
            isReady: false,
            pickerSelection: 'default',
            pickerDisplay: false
        };
    }
    async componentDidMount() {

        await Font.loadAsync({
            'Cafe24Ohsquare': require('../assets/fonts/Cafe24Ohsquare.ttf'),
            'Cafe24Ohsquareair': require('../assets/fonts/Cafe24Ohsquareair.ttf'),

        });

        this.setState({ isReady: true });
    }

    setPickerValue(newValue) {
        this.setState({
            pickerSelection: newValue
        })
        this.togglePicker();
    }

    togglePicker() {
        this.setState({
            pickerDisplay: !this.setState.pickerDisplay
        })
    }

    addProfile = (age, address, Quintile, gender, family) => {
        var user = firebase.auth().currentUser;
        try {
            if (user != null) {
                firebase.firestore().collection('users').doc(user.uid)
                    .update({
                        age,
                        address,
                        Quintile,
                        gender,
                        family,
                        job
                    }).then(() => this.props.navigation.navigate('MainScreen'))
            }
        }
        catch (error) {
            alert(error)
        }
    }

    render() {
        const jobList = [
            {
                title: '학생',
                value: '학생'
            },
            {
                title: '회사원',
                value: '회사원'
            },
            {
                title: '실업',
                value: '실업'
            },
            {
                title: '무직',
                value: '무직'
            },
            {
                title: '소상공인',
                value: '소상공인'
            }
        ]
        return (
            <ScrollView>
                <Container styles={styles.container}>
                    <Text style={styles.title}>
                        SetProfile
                    </Text>
                    <Text style={styles.subtitle}>
                        CoWel 어플리케이션 사용을 위한 개인정보 입력 폼입니다. 추후, 맞춤 검색 시 사용됩니다.
                    </Text>
                    <Form>
                        <Item floatingLabel>
                            <Label style={{ fontFamily: 'Cafe24Ohsquareair' }}>나이</Label>
                            <Input
                                onChangeText={(age) => this.setState({ age })}
                                style={styles.form}>
                            </Input>
                        </Item>

                        <Item floatingLabel>
                            <Label style={{ fontFamily: 'Cafe24Ohsquareair' }}>주소 (시군구)</Label>
                            <Input
                                onChangeText={(address) => this.setState({ address })}
                                style={styles.form}>
                            </Input>
                        </Item>

                        <Item floatingLabel>
                            <Label style={{ fontFamily: 'Cafe24Ohsquareair' }}>소득분위</Label>
                            <Input
                                onChangeText={(Quintile) => this.setState({ Quintile })}
                                style={styles.form}>
                            </Input>
                        </Item>

                        <Item floatingLabel>
                            <Label style={{ fontFamily: 'Cafe24Ohsquareair' }}>성별</Label>
                            <Input
                                onChangeText={(gender) => this.setState({ gender })}
                                style={styles.form}>
                            </Input>
                        </Item>

                        <Item floatingLabel>
                            <Label style={{ fontFamily: 'Cafe24Ohsquareair' }}>가구 수</Label>
                            <Input
                                onChangeText={(family) => this.setState({ family })}
                                style={styles.form}>
                            </Input>
                        </Item>
                        <Item floatingLabel>
                            <Label style={{ fontFamily: 'Cafe24Ohsquareair' }}>직업</Label>
                            <Modal visible={this.state.pickerDisplay} animationType={"silde"} transparent={true}>
                                <View style={{
                                    margin: 20, padding: 20,
                                    backgroundColor: '#efefef',
                                    bottom: 20, left: 20, right: 20,
                                    alignItems: 'center', position: 'absolute'
                                }}>
                                    {jobList.map((value, index) => {
                                        return <TouchableHighlight key={index} onPress={() => this.setPickerValue(value.value)} style={{ paddingTop: 4, paddingBottom: 4 }}>
                                            <Text>{value.title}</Text>
                                        </TouchableHighlight>
                                    })}

                                    <TouchableHighlight onPress={() => this.togglePicker()} style={{ paddingTop: 4, paddingBottom: 4 }}>
                                        <Text style={{ color: '#999' }}>Cancel</Text>
                                    </TouchableHighlight>
                                </View>
                            </Modal>
                        </Item>
                        <Button
                            title="save"
                            style={styles.button_ok}
                            rounded
                            backgroundColor='tomato'
                            onPress={() => this.addProfile(this.state.age, this.state.address, this.state.Quintile, this.state.gender, this.state.family, this.state.job)}>
                            <Text style={styles.button_text}>저장</Text>
                        </Button>

                    </Form>
                </Container>
            </ScrollView>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: "center",
        padding: 20
    },
    user: {
        fontFamily: 'Cafe24Ohsquareair',
        padding: 10,
        alignSelf: "flex-end",
        marginTop: 10
    },
    title: {
        fontFamily: 'Cafe24Ohsquareair',
        fontSize: 20,
        justifyContent: 'center',
        textAlign: 'center',
        marginTop: 50
    },
    subtitle: {
        fontFamily: 'Cafe24Ohsquareair',
        fontSize: 15,
        justifyContent: 'center',
        textAlign: 'center',
        marginTop: 10
    },
    form: {
        marginTop: 20,
        marginBottom: 20,
        marginRight: 20,
        marginLeft: 20,
        fontFamily: "Cafe24Ohsquareair"
    },
    button_text: {
        color: "white",
        alignItems: "center",
        justifyContent: "center",
        fontFamily: "Cafe24Ohsquareair",
        fontSize: 25
    },
    button_ok: {
        marginBottom: 10,
        marginTop: 30,
        padding: 30,
        width: 200,
        alignSelf: "center",
        justifyContent: "center",
        backgroundColor: "tomato"
    },
})
